import React, { useState, useEffect } from "react";
import axios from "axios";
import Calendar from "react-calendar"; // Importamos la biblioteca de calendario
import "react-calendar/dist/Calendar.css"; // Importamos los estilos
import "./ReservasPage.css";

const ReservasPage = () => {
  const [reservas, setReservas] = useState([]);
  const [espacios, setEspacios] = useState([]);
  const [espacioSeleccionado, setEspacioSeleccionado] = useState(null);
  const [fechaSeleccionada, setFechaSeleccionada] = useState(new Date());
  const [restricciones, setRestricciones] = useState({ horarioInicio: "06:00", horarioFin: "20:00" });

  useEffect(() => {
    const fetchEspacios = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/espacios");
        setEspacios(response.data);
        if (response.data.length > 0) {
          seleccionarEspacio(response.data[0]._id); // Selecciona el primer espacio
        }
      } catch (error) {
        console.error("Error al cargar espacios:", error);
      }
    };
    fetchEspacios();
  }, []);

  const seleccionarEspacio = async (idEspacio) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/espacios/${idEspacio}`);
      setEspacioSeleccionado(response.data);
      setRestricciones({
        horarioInicio: response.data.horarioInicio,
        horarioFin: response.data.horarioFin,
      });

      // Obtener reservas del espacio seleccionado
      const reservasResponse = await axios.get("http://localhost:5000/api/reservas", {
        params: { espacio: response.data.nombre },
      });
      setReservas(reservasResponse.data);
    } catch (error) {
      console.error("Error al seleccionar el espacio:", error);
    }
  };

  const marcarReservasEnCalendario = ({ date }) => {
    const fecha = date.toISOString().split("T")[0];
    return reservas.some((reserva) => reserva.fecha.startsWith(fecha)) ? "highlight" : null;
  };

  const manejarCambioFecha = (date) => {
    setFechaSeleccionada(date);
  };

  return (
    <div className="reservas-container">
      <h2>Reservar Espacios Comunes</h2>
      <p>Este espacio se reserva por un mínimo de 8 horas.</p>

      <div className="calendario-container">
        <Calendar
          onChange={manejarCambioFecha}
          value={fechaSeleccionada}
          tileClassName={marcarReservasEnCalendario}
        />
      </div>

      {fechaSeleccionada && (
        <div className="detalle-reservas">
          <h3>Reservas del día {fechaSeleccionada.toLocaleDateString()}</h3>
          <ul>
            {reservas
              .filter(
                (reserva) =>
                  new Date(reserva.fecha).toISOString().split("T")[0] ===
                  fechaSeleccionada.toISOString().split("T")[0]
              )
              .map((reserva) => (
                <li key={reserva._id}>
                  Usuario: {reserva.usuario} - Hora: {new Date(reserva.fecha).toLocaleTimeString()}
                </li>
              ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default ReservasPage;
