import React, { useState } from "react";

function AdminPaymentsPage() {
  const [userId, setUserId] = useState("");
  const [amount, setAmount] = useState("");
  const [dueDate, setDueDate] = useState("");

  const handleSetPayment = async () => {
    try {
      const response = await fetch("/api/payments/admin/set-payment", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ userId, amount, dueDate }),
      });

      const data = await response.json();
      alert(data.message);
    } catch (error) {
      console.error("Error:", error);
      alert("Error al configurar el pago");
    }
  };

  return (
    <div>
      <h2>Configurar Pagos</h2>
      <input
        type="text"
        placeholder="ID del usuario"
        value={userId}
        onChange={(e) => setUserId(e.target.value)}
      />
      <input
        type="number"
        placeholder="Monto"
        value={amount}
        onChange={(e) => setAmount(e.target.value)}
      />
      <input
        type="date"
        value={dueDate}
        onChange={(e) => setDueDate(e.target.value)}
      />
      <button onClick={handleSetPayment}>Configurar Pago</button>
    </div>
  );
}

export default AdminPaymentsPage;
