import React, { useState, useEffect } from "react";
import axios from "axios";
import "./ReservasPage.css";

const ReservasPage = () => {
  const [reservas, setReservas] = useState([]);
  const [espacios, setEspacios] = useState([]); // Espacios disponibles
  const [espacio, setEspacio] = useState("");
  const [fecha, setFecha] = useState("");
  const [message, setMessage] = useState("");

  useEffect(() => {
    const fetchDatos = async () => {
      try {
        // Obtener reservas
        const reservasResponse = await axios.get("http://localhost:5000/api/reservas", {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        setReservas(reservasResponse.data);

        // Obtener espacios comunes
        const espaciosResponse = await axios.get("http://localhost:5000/api/espacios");
        setEspacios(espaciosResponse.data);
      } catch (error) {
        console.error("Error al cargar datos:", error);
      }
    };

    fetchDatos();
  }, []);

  const handleCrearReserva = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        "http://localhost:5000/api/reservas",
        { espacio, fecha },
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      );
      setMessage(response.data.message);
      setReservas([...reservas, response.data.reserva]);
    } catch (error) {
      setMessage("Error al crear la reserva: " + (error.response?.data?.message || error.message));
    }
  };

  return (
    <div className="reservas-container">
      <h2>Reservar Espacios Comunes</h2>
      <form onSubmit={handleCrearReserva}>
        <div>
          <label>Espacio:</label>
          <select
            value={espacio}
            onChange={(e) => setEspacio(e.target.value)}
            required
          >
            <option value="">Seleccione un espacio</option>
            {espacios.map((esp) => (
              <option key={esp._id} value={esp.nombre}>
                {esp.nombre}
              </option>
            ))}
          </select>
        </div>
        <div>
          <label>Fecha:</label>
          <input
            type="datetime-local"
            value={fecha}
            onChange={(e) => setFecha(e.target.value)}
            required
          />
        </div>
        <button type="submit">Crear Reserva</button>
      </form>
      {message && <p>{message}</p>}

      <h3>Mis Reservas</h3>
      <ul>
        {reservas.map((reserva) => (
          <li key={reserva._id}>
            {reserva.espacio} - {new Date(reserva.fecha).toLocaleString()} - {reserva.estado}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ReservasPage;
