import React, { useState, useEffect } from "react";
import axios from "axios";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import "./ReservasPage.css";

const ReservasPage = () => {
  const [espacios, setEspacios] = useState([]);
  const [espacioSeleccionado, setEspacioSeleccionado] = useState(""); // ID del espacio seleccionado
  const [reservas, setReservas] = useState([]);
  const [fechaSeleccionada, setFechaSeleccionada] = useState(new Date());
  const [message, setMessage] = useState("");
  const [horaInicio, setHoraInicio] = useState("");
  const [restricciones, setRestricciones] = useState({ inicio: "06:00", fin: "20:00" }); // Horarios permitidos
  const [showPopup, setShowPopup] = useState(false); // Controla la visibilidad del pop-up

  // Cargar espacios al iniciar
  useEffect(() => {
    const fetchEspacios = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/espacios");
        setEspacios(response.data);
        if (response.data.length > 0) {
          setEspacioSeleccionado(response.data[0]._id); // Selecciona el primer espacio automáticamente
        }
      } catch (error) {
        console.error("Error al cargar espacios:", error);
      }
    };

    fetchEspacios();
  }, []);

  // Cargar restricciones horarias al cambiar espacio seleccionado
  useEffect(() => {
    const fetchRestricciones = async () => {
      try {
        if (espacioSeleccionado) {
          const response = await axios.get(`http://localhost:5000/api/espacios/${espacioSeleccionado}`);
          setRestricciones(response.data.restriccionesHorarias);
        }
      } catch (error) {
        console.error("Error al cargar restricciones horarias:", error);
      }
    };

    fetchRestricciones();
  }, [espacioSeleccionado]);

  // Cargar reservas al cambiar espacio seleccionado
  useEffect(() => {
    const fetchReservas = async () => {
      try {
        if (espacioSeleccionado) {
          const response = await axios.get("http://localhost:5000/api/reservas", {
            params: { espacio: espacioSeleccionado }, // Enviar ID del espacio como parámetro
          });
          setReservas(response.data); // Actualiza la lista de reservas
        }
      } catch (error) {
        console.error("Error al cargar reservas:", error);
      }
    };

    fetchReservas();
  }, [espacioSeleccionado]); // Se ejecuta cada vez que cambia el espacio seleccionado

  // Lógica para manejar la creación de reservas
  const handleCrearReserva = async () => {
    try {
      if (!horaInicio) {
        setMessage("Por favor, selecciona una hora de inicio.");
        return;
      }

      const response = await axios.post(
        "http://localhost:5000/api/reservas",
        {
          espacio: espacioSeleccionado,
          fecha: `${fechaSeleccionada.toISOString().split("T")[0]}T${horaInicio}:00`, // Combina fecha y hora
        },
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      );

      setMessage("Reserva creada exitosamente");
      setReservas([...reservas, response.data.reserva]); // Actualiza la lista de reservas
      setShowPopup(false); // Cierra el pop-up
    } catch (error) {
      setMessage("Error al crear la reserva");
    }
  };

  // Generar horas dinámicamente entre las restricciones horarias
  const generarHoras = () => {
    const [inicioHora] = restricciones.inicio.split(":").map(Number);
    const [finHora] = restricciones.fin.split(":").map(Number);

    const horas = [];
    for (let hora = inicioHora; hora < finHora; hora++) {
      horas.push(`${hora.toString().padStart(2, "0")}:00`);
    }
    return horas;
  };

  return (
    <div className="reservas-container">
      <h2>Reservar Espacios Comunes</h2>
      <p>Este espacio se reserva por un mínimo de 8 horas.</p>

      {/* Selector de espacios */}
      <div>
        <label>Seleccione un espacio:</label>
        <select
          value={espacioSeleccionado}
          onChange={(e) => setEspacioSeleccionado(e.target.value)}
        >
          {espacios.map((espacio) => (
            <option key={espacio._id} value={espacio._id}>
              {espacio.nombre}
            </option>
          ))}
        </select>
      </div>

      {/* Calendario */}
      <div className="calendario-container">
        <Calendar
          onChange={(date) => setFechaSeleccionada(date)}
          value={fechaSeleccionada}
        />
      </div>

      {/* Selección de hora */}
      <div className="hora-container">
        <label>Seleccione la hora de inicio:</label>
        <select value={horaInicio} onChange={(e) => setHoraInicio(e.target.value)}>
          <option value="">Selecciona una hora</option>
          {generarHoras().map((hora) => (
            <option key={hora} value={hora}>
              {hora}
            </option>
          ))}
        </select>
      </div>

      {/* Botón para abrir el pop-up */}
      <button onClick={() => setShowPopup(true)}>Solicitar reserva</button>

      {/* Pop-up de confirmación */}
      {showPopup && (
        <div className="popup-overlay">
          <div className="popup">
            <h3>Confirmación de Reserva</h3>
            <p>
              Para confirmar la reserva del espacio es necesario pagar la mitad por adelantado. 
              Si está de acuerdo, haga clic en "Continuar".
            </p>
            <button className="btn-continuar" onClick={handleCrearReserva}>
              Continuar
            </button>
            <button className="btn-cancelar" onClick={() => setShowPopup(false)}>
              Cancelar
            </button>
          </div>
        </div>
      )}

      {/* Mensaje */}
      {message && <p>{message}</p>}

      {/* Listar reservas */}
      <h3>Reservas del espacio seleccionado</h3>
      <ul>
        {reservas.map((reserva) => {
          const fechaReserva = new Date(reserva.fecha);
          return (
            <li key={reserva._id}>
              Fecha: {fechaReserva.toLocaleDateString()} - Hora:{" "}
              {fechaReserva.toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" })} - Usuario:{" "}
              {reserva.usuario?.name || "Desconocido"}
            </li>
          );
        })}
      </ul>
    </div>
  );
};

export default ReservasPage;
